-- ----------------------------
-- Table structure for vendor
-- ----------------------------
DROP TABLE IF EXISTS `vendor`;
CREATE TABLE `vendor` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '供应商主键ID，自增',
  `name` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '供应商名称',
  `contact_person` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '供应商联系人',
  `contact_phone` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '供应商联系电话',
  `email` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '电子邮箱',
  `product_type` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '产品类型',
  `work_address` varchar(255) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '办公地址',
  `user_id` int(11) DEFAULT NULL COMMENT '用户ID',
  `is_audit` tinyint(1) NOT NULL COMMENT '是否已经审核',
  `status` tinyint(1) NOT NULL COMMENT '供应商的状态',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin;

